theme.customCss:
.typebot-guest-bubble,
.typebot-host-bubble,
.typebot-input-container {
  font-size: 1.375rem;
}

.typebot-input-container:has(input) {
  border-radius: 5rem;
  position: absolute;
  bottom: 3rem;
  width: calc(100% - 40px);
  justify-content: flex-start;
  background-color: #fff;
  border: 1px solid #E2D5BF;
}

.typebot-input-form {
  max-width: unset
}

.typebot-input-form > button {
  border-radius: 5rem;
  transform: scale(.7);
}

.bubble-typing,
.slate-html-container,
.typebot-host-bubble > figure{
  padding: 0;
  margin: 0
}

.slate-html-container {
  margin-bottom: .5rem
}
